Facility BeginToReason;
    uses Integer_Ext_Theory;

    Operation Main();
    Procedure
        Var I, J, K: Integer;
        Read(I);
        Read(J);
        Read(K);

        Remember;

        If I <= K then
            K := I;
        end;
        If J <= K then
            K := J;
        end;

        Confirm K /*conditional*/ #I;
        Confirm K /*conditional*/ #J;
        Confirm K /*conditional*/ #K;
    end Main;
end BeginToReason;
